<template>
    <div>
      <div v-if="showToast" id="mubiao">
        <div class="avatar"><img
          src="data:image/png;base64,"
          alt=""/>
        </div>
        <h3 class="nickname">貌似只能在微信浏览器执行哦</h3>
        <p class="wisdom">H5页面快照生成 demo</p>
      </div>
      <div id="shareImg" class="shareImg"></div>
      <div @click="snapshot">点击生成图爿</div>
    </div>
</template>

<script>
import html2canvas from 'html2canvas'
export default {
  name: 'PlayPlay',
  created () {
  },
  data () {
    return {
      showToast: true,
      options: {
        allowTaint: true,
        useCORS: true
      }
    }
  },
  methods: {
    snapshot () {
      alert(this.options.allowTaint)
      this.showToast = false
      html2canvas(document.getElementById('mubiao'), this.options).then(function (canvas) {
        var url = canvas.toDataURL('image/png')// base64数据
        var image = new Image()
        image.src = url
        document.getElementById('shareImg').appendChild(image)
      })
    }
  }
}
</script>

<style lang="scss" scoped>
#mubiao {
  font-size: 0.24rem;
}
</style>
